INSERT INTO public.assessments VALUES (3, 4, '{"no_of_questions": 6, "questions": [{"question": "Which word is a noun (a person, place, or thing)?", "options": [{"a": "run"}, {"b": "happy"}, {"c": "school"}, {"d": "fast"}], "answer": "c"}, {"question": "Which word is a verb (an action word)?", "options": [{"a": "jump"}, {"b": "girl"}, {"c": "big"}, {"d": "table"}], "answer": "a"}, {"question": "Which word is an adjective (a describing word)?", "options": [{"a": "eat"}, {"b": "funny"}, {"c": "cat"}, {"d": "house"}], "answer": "b"}, {"question": "In the sentence ''The dog barks.'', which word is the noun?", "options": [{"a": "The"}, {"b": "dog"}, {"c": "barks"}, {"d": "is"}], "answer": "b"}, {"question": "In the sentence ''Birds fly high.'', which word is the verb?", "options": [{"a": "Birds"}, {"b": "high"}, {"c": "fly"}, {"d": "in"}], "answer": "c"}, {"question": "In the sentence ''The red car is fast.'', which word is an adjective?", "options": [{"a": "car"}, {"b": "is"}, {"c": "fast"}, {"d": "red"}], "answer": "d"}]}', '2025-10-09 18:06:16.220386');
INSERT INTO public.assessments VALUES (4, 5, '{"no_of_questions": 6, "questions": [{"question": "Which sentence uses a ''word picture'' to say the boy is fast?", "options": [{"a": "The boy runs fast."}, {"b": "The boy runs like the wind."}, {"c": "The boy likes to run."}, {"d": "The boy is a runner."}], "answer": "b"}, {"question": "The ice cream was as cold as ____.", "options": [{"a": "the sun"}, {"b": "a blanket"}, {"c": "ice"}, {"d": "a stove"}], "answer": "c"}, {"question": "Which sentence gives a ''people-action'' to the stars?", "options": [{"a": "The stars are in the sky."}, {"b": "The stars are very bright."}, {"c": "I can see the stars."}, {"d": "The stars winked at me."}], "answer": "d"}, {"question": "Her smile was like sunshine. What does this mean?", "options": [{"a": "Her smile was yellow."}, {"b": "Her smile was very bright and happy."}, {"c": "Her smile was hot."}, {"d": "She was smiling at the sun."}], "answer": "b"}, {"question": "Which of these is NOT a figure of speech?", "options": [{"a": "He is as strong as an ox."}, {"b": "The pillow is like a cloud."}, {"c": "The cake is delicious."}, {"d": "The angry storm yelled at the town."}], "answer": "c"}, {"question": "The teacher''s voice was as soft as a ____.", "options": [{"a": "rock"}, {"b": "whisper"}, {"c": "drum"}, {"d": "horn"}], "answer": "b"}]}', '2025-10-09 20:52:17.689436');
INSERT INTO public.assessments VALUES (5, 6, '{"no_of_questions": 6, "questions": [{"question": "Which word means the SAME as ''happy''?", "options": [{"a": "sad"}, {"b": "glad"}, {"c": "angry"}, {"d": "tired"}], "answer": "b"}, {"question": "Which word is the OPPOSITE of ''hot''?", "options": [{"a": "warm"}, {"b": "sunny"}, {"c": "cold"}, {"d": "fire"}], "answer": "c"}, {"question": "A synonym is a word that means the...", "options": [{"a": "opposite"}, {"b": "same"}, {"c": "sound"}, {"d": "letter"}], "answer": "b"}, {"question": "Find the synonym for ''small''.", "options": [{"a": "big"}, {"b": "tall"}, {"c": "wide"}, {"d": "little"}], "answer": "d"}, {"question": "An antonym is a word that is the...", "options": [{"a": "same"}, {"b": "rhyme"}, {"c": "opposite"}, {"d": "picture"}], "answer": "c"}, {"question": "Find the antonym for ''fast''.", "options": [{"a": "quick"}, {"b": "slow"}, {"c": "run"}, {"d": "stop"}], "answer": "b"}]}', '2025-10-10 09:32:44.463124');
INSERT INTO public.assessments VALUES (6, 7, '{"no_of_questions": 6, "questions": [{"question": "What is the primary definition of a computer?", "options": [{"a": "A device for watching videos"}, {"b": "An electronic machine that processes data"}, {"c": "A tool for playing games"}, {"d": "A machine that only uses electricity"}], "answer": "b"}, {"question": "Which of the following is an example of an input device?", "options": [{"a": "Monitor"}, {"b": "Printer"}, {"c": "Keyboard"}, {"d": "Speakers"}], "answer": "c"}, {"question": "What part of the computer is often called the ''brain''?", "options": [{"a": "Monitor"}, {"b": "Mouse"}, {"c": "CPU (Central Processing Unit)"}, {"d": "Keyboard"}], "answer": "c"}, {"question": "When you see words or pictures on the screen, what is this called?", "options": [{"a": "Input"}, {"b": "Output"}, {"c": "Processing"}, {"d": "Storage"}], "answer": "b"}, {"question": "The four main functions of a computer are Input, Processing, Output, and _________.", "options": [{"a": "Starting"}, {"b": "Stopping"}, {"c": "Storage"}, {"d": "Sharing"}], "answer": "c"}, {"question": "Saving your homework file on the computer is an example of which function?", "options": [{"a": "Input"}, {"b": "Output"}, {"c": "Processing"}, {"d": "Storage"}], "answer": "d"}]}', '2025-10-10 21:55:50.899176');
INSERT INTO public.assessments VALUES (7, 8, '{"no_of_questions": 6, "questions": [{"question": "Which word means the same as ''happy''?", "options": [{"a": "sad"}, {"b": "glad"}, {"c": "mad"}, {"d": "tired"}], "answer": "b"}, {"question": "What is the opposite of ''hot''?", "options": [{"a": "warm"}, {"b": "sunny"}, {"c": "cold"}, {"d": "fire"}], "answer": "c"}, {"question": "A synonym for ''small'' is...", "options": [{"a": "big"}, {"b": "tall"}, {"c": "wide"}, {"d": "tiny"}], "answer": "d"}, {"question": "An antonym for ''fast'' is...", "options": [{"a": "quick"}, {"b": "slow"}, {"c": "run"}, {"d": "jet"}], "answer": "b"}, {"question": "Which word means the same as ''big''?", "options": [{"a": "large"}, {"b": "little"}, {"c": "short"}, {"d": "thin"}], "answer": "a"}, {"question": "What is the opposite of ''up''?", "options": [{"a": "sky"}, {"b": "high"}, {"c": "down"}, {"d": "top"}], "answer": "c"}]}', '2025-10-11 16:22:50.020933');
INSERT INTO public.assessments VALUES (8, 9, '{"no_of_questions": 5, "questions": [{"question": "What is the best definition of prose?", "options": [{"a": "Writing that always rhymes"}, {"b": "Ordinary writing in sentences and paragraphs"}, {"c": "Writing that is only found in songs"}, {"d": "A special type of a short poem"}], "answer": "b"}, {"question": "Which of the following is an example of prose?", "options": [{"a": "A nursery rhyme"}, {"b": "A short story"}, {"c": "The lyrics to a song"}, {"d": "A haiku"}], "answer": "b"}, {"question": "Prose is usually organized into...", "options": [{"a": "Stanzas and verses"}, {"b": "Rhyming couplets"}, {"c": "Sentences and paragraphs"}, {"d": "Musical notes"}], "answer": "c"}, {"question": "How is prose different from poetry?", "options": [{"a": "Prose does not use rhyme or a specific rhythm"}, {"b": "Prose is always longer than poetry"}, {"c": "Prose is only about true events"}, {"d": "There is no difference"}], "answer": "a"}, {"question": "Where would you most likely find prose?", "options": [{"a": "In a songbook"}, {"b": "In a book of poems"}, {"c": "In a birthday card with a rhyming verse"}, {"d": "In a newspaper article"}], "answer": "d"}]}', '2025-10-11 21:56:02.174144');
INSERT INTO public.assessments VALUES (9, 10, '{"no_of_questions": 5, "questions": [{"question": "If your friend tells you to ''break a leg'' before your school play, what do they mean?", "options": [{"a": "They want you to get hurt."}, {"b": "They are wishing you good luck."}, {"c": "They think you are a bad actor."}, {"d": "They want you to break something on stage."}], "answer": "b"}, {"question": "What does the idiom ''spill the beans'' mean?", "options": [{"a": "To make a mess with food."}, {"b": "To be clumsy."}, {"c": "To tell a secret."}, {"d": "To plant a garden."}], "answer": "c"}, {"question": "If a math test was ''a piece of cake'', it means the test was...", "options": [{"a": "very easy."}, {"b": "delicious."}, {"c": "very difficult."}, {"d": "about baking."}], "answer": "a"}, {"question": "The phrase ''when pigs fly'' is used to describe something that...", "options": [{"a": "will happen soon."}, {"b": "is very funny."}, {"c": "happens at a farm."}, {"d": "will never happen."}], "answer": "d"}, {"question": "My new video game ''costs an arm and a leg''. What does this mean?", "options": [{"a": "It was free."}, {"b": "It was very cheap."}, {"c": "It was very expensive."}, {"d": "It is a violent game."}], "answer": "c"}]}', '2025-10-11 21:58:34.848432');
INSERT INTO public.assessments VALUES (11, 12, '{"no_of_questions": 6, "questions": [{"question": "Which of the following events is CERTAIN to happen?", "options": [{"a": "You will win a video game."}, {"b": "The sun will rise tomorrow morning."}, {"c": "It will rain next Tuesday."}, {"d": "You will find a dollar on the street."}], "answer": "b"}, {"question": "If you roll a standard six-sided die, which of the following is IMPOSSIBLE?", "options": [{"a": "Rolling a 6"}, {"b": "Rolling a 3"}, {"c": "Rolling a number less than 5"}, {"d": "Rolling a 7"}], "answer": "d"}, {"question": "A bag contains 8 red marbles and 2 blue marbles. What is the likelihood of picking a blue marble without looking?", "options": [{"a": "Certain"}, {"b": "Likely"}, {"c": "Unlikely"}, {"d": "Impossible"}], "answer": "c"}, {"question": "If you flip a fair coin, the chances of it landing on heads or tails are:", "options": [{"a": "Impossible"}, {"b": "Equally likely"}, {"c": "Unlikely"}, {"d": "Certain"}], "answer": "b"}, {"question": "A spinner is divided into 4 equal sections: red, green, blue, and yellow. What is the probability of landing on blue?", "options": [{"a": "1/4"}, {"b": "1/2"}, {"c": "3/4"}, {"d": "4/4"}], "answer": "a"}, {"question": "There are 10 candies in a jar: 7 are cherry and 3 are lemon. It is _________ that you will pick a cherry candy.", "options": [{"a": "Impossible"}, {"b": "Unlikely"}, {"c": "Likely"}, {"d": "Certain"}], "answer": "c"}]}', '2025-10-11 22:04:29.214724');
INSERT INTO public.assessments VALUES (21, 22, '{"no_of_questions": 8, "questions": [{"question": "What is the next number in the sequence: 3, 6, 9, 12, __?", "options": [{"a": "13"}, {"b": "15"}, {"c": "14"}, {"d": "16"}], "answer": "b"}, {"question": "Find the missing number in the sequence: 10, 20, __, 40, 50.", "options": [{"a": "25"}, {"b": "22"}, {"c": "35"}, {"d": "30"}], "answer": "d"}, {"question": "What is the rule for this sequence: 25, 20, 15, 10?", "options": [{"a": "Add 5"}, {"b": "Add 10"}, {"c": "Subtract 5"}, {"d": "Subtract 10"}], "answer": "c"}, {"question": "What is the next number in the sequence: 44, 42, 40, 38, __?", "options": [{"a": "36"}, {"b": "37"}, {"c": "34"}, {"d": "39"}], "answer": "a"}, {"question": "Which sequence follows the rule ''Add 4''?", "options": [{"a": "1, 4, 8, 12"}, {"b": "4, 14, 24, 34"}, {"c": "5, 10, 15, 20"}, {"d": "2, 6, 10, 14"}], "answer": "d"}, {"question": "Find the missing number in the sequence: 11, 22, __, 44, 55.", "options": [{"a": "30"}, {"b": "33"}, {"c": "29"}, {"d": "40"}], "answer": "b"}, {"question": "What is the rule for this sequence: 7, 14, 21, 28?", "options": [{"a": "Add 7"}, {"b": "Subtract 7"}, {"c": "Add 14"}, {"d": "Add 1"}], "answer": "a"}, {"question": "A sequence starts at 50 and the rule is ''Subtract 10''. What is the third number in the sequence?", "options": [{"a": "40"}, {"b": "20"}, {"c": "30"}, {"d": "60"}], "answer": "c"}]}', '2025-10-11 22:11:34.421147');
INSERT INTO public.assessments VALUES (26, 27, '{"no_of_questions": 6, "questions": [{"question": "The cat _______ on the mat.", "options": [{"a": "sleep"}, {"b": "sleeps"}, {"c": "are sleeping"}, {"d": "were sleeping"}], "answer": "b"}, {"question": "My friends _______ to the park.", "options": [{"a": "goes"}, {"b": "is going"}, {"c": "go"}, {"d": "was going"}], "answer": "c"}, {"question": "A girl in my class _______ a beautiful new bike.", "options": [{"a": "have"}, {"b": "are having"}, {"c": "is"}, {"d": "has"}], "answer": "d"}, {"question": "The birds _______ in the sky.", "options": [{"a": "is"}, {"b": "are"}, {"c": "was"}, {"d": "am"}], "answer": "b"}, {"question": "He _______ his homework every day.", "options": [{"a": "do"}, {"b": "are doing"}, {"c": "does"}, {"d": "were doing"}], "answer": "c"}, {"question": "We _______ happy to see the sunshine.", "options": [{"a": "is"}, {"b": "was"}, {"c": "am"}, {"d": "are"}], "answer": "d"}]}', '2025-10-11 22:15:13.534872');
INSERT INTO public.assessments VALUES (31, 32, '{"no_of_questions": 5, "questions": [{"question": "What does a linear graph look like?", "options": [{"a": "A curvy line"}, {"b": "A straight line"}, {"c": "A circle"}, {"d": "A square"}], "answer": "b"}, {"question": "On a graph, which axis is the horizontal line that goes from left to right?", "options": [{"a": "The y-axis"}, {"b": "The vertical axis"}, {"c": "The x-axis"}, {"d": "The origin axis"}], "answer": "c"}, {"question": "To plot the point (2, 5), where do you start?", "options": [{"a": "Go up 2, then right 5"}, {"b": "Go right 5, then up 2"}, {"c": "Go left 2, then down 5"}, {"d": "Go right 2, then up 5"}], "answer": "d"}, {"question": "If you plot the points (1, 3), (2, 4), and (3, 5), what will you create when you connect them?", "options": [{"a": "A zigzag line"}, {"b": "A U-shape"}, {"c": "A straight line"}, {"d": "A random shape"}], "answer": "c"}, {"question": "Which of these points would NOT be on a straight line created by the pattern (2, 2), (3, 3), (4, 4)?", "options": [{"a": "(5, 5)"}, {"b": "(1, 1)"}, {"c": "(5, 6)"}, {"d": "(6, 6)"}], "answer": "c"}]}', '2025-10-11 22:18:14.828019');
INSERT INTO public.assessments VALUES (32, 33, '{"no_of_questions": 6, "questions": [{"question": "What is a ''language register''?", "options": [{"a": "A type of book in the library"}, {"b": "The way we change our language for different situations"}, {"c": "A list of new vocabulary words"}, {"d": "The speed at which we talk"}], "answer": "b"}, {"question": "In which of these situations would you use a FORMAL language register?", "options": [{"a": "Talking to your best friend during recess"}, {"b": "Texting your brother a funny joke"}, {"c": "Giving a speech to the entire school"}, {"d": "Playing a game at home with your family"}], "answer": "c"}, {"question": "Which of these situations calls for an INFORMAL language register?", "options": [{"a": "Writing a letter to the mayor"}, {"b": "Chatting with your friends on the playground"}, {"c": "An interview for a school leadership position"}, {"d": "Asking your teacher for help with a math problem"}], "answer": "b"}, {"question": "Which sentence is written in a formal register?", "options": [{"a": "I''m gonna be late for the thing."}, {"b": "Hey, wait up!"}, {"c": "I would like to request an extension on the assignment."}, {"d": "Can''t believe you did that, LOL."}], "answer": "c"}, {"question": "Your friend says, ''I am delighted to make your acquaintance.'' Which register is he trying to use?", "options": [{"a": "Informal"}, {"b": "Formal"}, {"c": "Slang"}, {"d": "Texting"}], "answer": "b"}, {"question": "How would you change the informal sentence ''Can I get some water?'' to a formal one?", "options": [{"a": "Gimme water now."}, {"b": "I''m super thirsty."}, {"c": "Where''s the water at?"}, {"d": "May I please have some water?"}], "answer": "d"}]}', '2025-10-11 22:18:44.71125');
INSERT INTO public.assessments VALUES (10, 11, '{"no_of_questions": 5, "questions": [{"question": "Which punctuation mark should be at the end of this sentence: What is your favorite color", "options": [{"a": "."}, {"b": ","}, {"c": "!"}, {"d": "?"}], "answer": "d"}, {"question": "What is the NOUN in the sentence: ''The cat sat on the mat.''?", "options": [{"a": "The"}, {"b": "cat"}, {"c": "sat"}, {"d": "on"}], "answer": "b"}, {"question": "What is the VERB in the sentence: ''She runs very fast.''?", "options": [{"a": "She"}, {"b": "runs"}, {"c": "very"}, {"d": "fast"}], "answer": "b"}, {"question": "Which sentence needs a comma?", "options": [{"a": "I have a blue bike."}, {"b": "My dog is friendly."}, {"c": "We ate pizza salad and bread."}, {"d": "He ran home."}], "answer": "c"}, {"question": "What is the ADJECTIVE in the sentence: ''He wore a bright, yellow shirt.''?", "options": [{"a": "He"}, {"b": "wore"}, {"c": "shirt"}, {"d": "yellow"}], "answer": "d"}]}', '2025-10-11 21:59:32.962422');
INSERT INTO public.assessments VALUES (12, 13, '{"no_of_questions": 7, "questions": [{"question": "What are the three main parts of an essay?", "options": [{"a": "Greeting, Body, Signature"}, {"b": "Introduction, Body, Conclusion"}, {"c": "Date, Title, Ending"}, {"d": "Beginning, Middle, Photo"}], "answer": "b"}, {"question": "Which part of an essay contains the main details and information?", "options": [{"a": "The Introduction"}, {"b": "The Title"}, {"c": "The Body"}, {"d": "The Conclusion"}], "answer": "c"}, {"question": "What is the part of a letter where you say ''hello'', like ''Dear Maria,''?", "options": [{"a": "The Date"}, {"b": "The Closing"}, {"c": "The Signature"}, {"d": "The Greeting"}], "answer": "d"}, {"question": "Where do you sign your name in a friendly letter?", "options": [{"a": "At the very top"}, {"b": "Before the greeting"}, {"c": "After the closing"}, {"d": "In the middle of the body"}], "answer": "c"}, {"question": "Which of these is NOT a part of a friendly letter?", "options": [{"a": "Date"}, {"b": "Conclusion"}, {"c": "Body"}, {"d": "Closing"}], "answer": "b"}, {"question": "If you wanted to write to your grandma to thank her for a gift, you would write a...", "options": [{"a": "Essay"}, {"b": "Poem"}, {"c": "Letter"}, {"d": "Report"}], "answer": "c"}, {"question": "The purpose of an essay is usually to...", "options": [{"a": "Ask someone a question"}, {"b": "Say hello to a friend"}, {"c": "Explain a topic or tell a story to an audience"}, {"d": "Invite someone to a party"}], "answer": "c"}]}', '2025-10-11 22:05:32.445842');
INSERT INTO public.assessments VALUES (13, 14, '{"no_of_questions": 4, "questions": [{"question": "What is a shape with three straight sides and three corners called?", "options": [{"a": "Circle"}, {"b": "Square"}, {"c": "Triangle"}, {"d": "Line"}], "answer": "c"}, {"question": "Which of these has four equal sides and four square corners?", "options": [{"a": "Triangle"}, {"b": "Square"}, {"c": "Circle"}, {"d": "Point"}], "answer": "b"}, {"question": "Which of the following is the best example of a circle?", "options": [{"a": "A book"}, {"b": "A door"}, {"c": "A pizza"}, {"d": "A pencil"}], "answer": "c"}, {"question": "What do we call a part of a line that has two endpoints?", "options": [{"a": "A line segment"}, {"b": "A full line"}, {"c": "A point"}, {"d": "A circle"}], "answer": "a"}]}', '2025-10-11 22:05:56.741619');
INSERT INTO public.assessments VALUES (14, 15, '{"no_of_questions": 10, "questions": [{"question": "Read the short passage: Maria loved to bake. On Saturday morning, she decided to make cookies for her friend, Sam. She mixed flour, sugar, and chocolate chips in a big blue bowl in her kitchen. The cookies were a surprise for his birthday. What is ''reading comprehension''?", "options": [{"a": "Guessing what a story is about"}, {"b": "Reading a story very fast"}, {"b": "Understanding what you have read"}, {"d": "Memorizing all the words"}], "answer": "c"}, {"question": "WHO is the main character in the passage?", "options": [{"a": "Sam"}, {"b": "The cookies"}, {"c": "The kitchen"}, {"d": "Maria"}], "answer": "d"}, {"question": "WHAT was Maria making?", "options": [{"a": "A cake"}, {"b": "Cookies"}, {"c": "A big mess"}, {"d": "Breakfast"}], "answer": "b"}, {"question": "WHERE was Maria baking?", "options": [{"a": "At Sam''s house"}, {"b": "At a bakery"}, {"c": "In her kitchen"}, {"d": "Outside"}], "answer": "c"}, {"question": "WHEN did Maria bake?", "options": [{"a": "On Saturday morning"}, {"b": "On Sunday afternoon"}, {"c": "After school"}, {"d": "At night"}], "answer": "a"}, {"question": "WHY was Maria making cookies?", "options": [{"a": "Because she was hungry"}, {"b": "For a school project"}, {"c": "As a birthday surprise for Sam"}, {"d": "To sell at a bake sale"}], "answer": "c"}, {"question": "Which of these is a key detail from the story?", "options": [{"a": "Maria has a red apron"}, {"b": "The bowl was big and blue"}, {"c": "Sam does not like chocolate chips"}, {"d": "It was raining outside"}], "answer": "b"}, {"question": "What is the main idea of the passage?", "options": [{"a": "The steps to bake cookies"}, {"b": "A girl is making a kind gift for her friend"}, {"c": "Saturday is the best day for baking"}, {"d": "Sam is having a birthday party"}], "answer": "b"}, {"question": "Finding the ''Who, What, and Where'' in a story helps you understand the:", "options": [{"a": "Author''s name"}, {"b": "Number of pages"}, {"c": "Basic details and setting"}, {"d": "Book''s price"}], "answer": "c"}, {"question": "Thinking about ''Why'' something happened in a story is part of:", "options": [{"a": "Analysis"}, {"b": "Spelling"}, {"c": "Guessing"}, {"d": "Skipping"}], "answer": "a"}]}', '2025-10-11 22:06:47.792904');
INSERT INTO public.assessments VALUES (15, 16, '{"no_of_questions": 7, "questions": [{"question": "In the equation 5 + ? = 12, what is the missing number?", "options": [{"a": "5"}, {"b": "6"}, {"c": "7"}, {"d": "8"}], "answer": "c"}, {"question": "If x + 4 = 10, what is the value of x?", "options": [{"a": "4"}, {"b": "6"}, {"c": "10"}, {"d": "14"}], "answer": "b"}, {"question": "A box (\u25a1) is hiding a number. If 15 - \u25a1 = 9, what number is in the box?", "options": [{"a": "5"}, {"b": "6"}, {"c": "7"}, {"d": "9"}], "answer": "b"}, {"question": "What does the letter ''a'' stand for in the equation 8 - a = 2?", "options": [{"a": "The answer"}, {"b": "The problem"}, {"c": "An unknown number"}, {"d": "The number 8"}], "answer": "c"}, {"question": "Find the value of ''n''.   n + 8 = 11", "options": [{"a": "3"}, {"b": "8"}, {"c": "11"}, {"d": "19"}], "answer": "a"}, {"question": "Which equation matches this story: ''I had some cookies. I ate 3, and now I have 5 left.''?", "options": [{"a": "c + 3 = 5"}, {"b": "c - 3 = 5"}, {"c": "3 - c = 5"}, {"d": "5 - 3 = c"}], "answer": "b"}, {"question": "Solve for the star (\u2606): 6 + \u2606 = 14", "options": [{"a": "6"}, {"b": "7"}, {"c": "8"}, {"d": "20"}], "answer": "c"}]}', '2025-10-11 22:07:43.220252');
INSERT INTO public.assessments VALUES (16, 17, '{"no_of_questions": 5, "questions": [{"question": "What is oral communication?", "options": [{"a": "Reading a book silently"}, {"b": "Writing a letter to a friend"}, {"c": "Talking and listening to someone"}, {"d": "Drawing a picture"}], "answer": "c"}, {"question": "When you are the SPEAKER, what is an important thing to do?", "options": [{"a": "Mumble your words"}, {"b": "Speak clearly so people can understand you"}, {"c": "Look at the ceiling"}, {"d": "Cover your mouth"}], "answer": "b"}, {"question": "Which of these is a good skill for a LISTENER?", "options": [{"a": "Interrupt the speaker often"}, {"b": "Think about what you want for lunch"}, {"c": "Play with your pencil"}, {"d": "Pay attention to what the speaker is saying"}], "answer": "d"}, {"question": "Which one is an example of using oral communication?", "options": [{"a": "Telling your friend about your weekend"}, {"b": "Sending an email"}, {"c": "Writing in a diary"}, {"d": "Reading a map"}], "answer": "a"}, {"question": "If your teacher is explaining a math problem to the class, your main job is to be a good...", "options": [{"a": "Speaker"}, {"b": "Writer"}, {"c": "Listener"}, {"d": "Reader"}], "answer": "c"}]}', '2025-10-11 22:08:20.946217');
INSERT INTO public.assessments VALUES (17, 18, '{"no_of_questions": 5, "questions": [{"question": "Which symbol means ''greater than''?", "options": [{"a": "<"}, {"b": ">"}, {"c": "="}, {"d": "+"}], "answer": "b"}, {"question": "Choose the correct symbol to make the statement true: 9 ___ 5", "options": [{"a": "<"}, {"b": "="}, {"c": ">"}, {"d": "-"}], "answer": "c"}, {"question": "Choose the correct symbol to make the statement true: 12 ___ 18", "options": [{"a": ">"}, {"b": "<"}, {"c": "="}, {"d": "x"}], "answer": "b"}, {"question": "Which of these statements is TRUE?", "options": [{"a": "10 > 15"}, {"b": "15 < 10"}, {"c": "15 = 10"}, {"d": "15 > 10"}], "answer": "d"}, {"question": "If you have 6 cookies and your friend has 6 cookies, which symbol would you use to compare the numbers?", "options": [{"a": ">"}, {"b": "<"}, {"c": "="}, {"d": "+"}], "answer": "c"}]}', '2025-10-11 22:08:36.726468');
INSERT INTO public.assessments VALUES (18, 19, '{"no_of_questions": 6, "questions": [{"question": "What is literature?", "options": [{"a": "Only books with numbers"}, {"b": "Written works like stories and poems"}, {"c": "Movies and TV shows"}, {"d": "Just a list of words"}], "answer": "b"}, {"question": "A story about a talking unicorn that lives on the moon is an example of...", "options": [{"a": "Fiction (make-believe)"}, {"b": "Non-Fiction (facts)"}, {"c": "A dictionary"}, {"d": "A recipe"}], "answer": "a"}, {"question": "Which of these is a reason people write literature?", "options": [{"a": "To make a list for the store"}, {"b": "To count things"}, {"c": "To share ideas and entertain"}, {"d": "To build a house"}], "answer": "c"}, {"question": "A book that teaches you true facts about how volcanoes work is...", "options": [{"a": "Fiction"}, {"b": "A comic book"}, {"c": "A poem"}, {"d": "Non-Fiction"}], "answer": "d"}, {"question": "''The cat sat on the mat. It was big and very fat.'' This is most likely from a...", "options": [{"a": "Poem"}, {"b": "Science report"}, {"c": "Map"}, {"d": "Math problem"}], "answer": "a"}, {"question": "All of the following are types of literature EXCEPT:", "options": [{"a": "Stories"}, {"b": "Poems"}, {"c": "Shopping lists"}, {"d": "Plays"}], "answer": "c"}]}', '2025-10-11 22:09:58.549756');
INSERT INTO public.assessments VALUES (23, 24, '{"no_of_questions": 5, "questions": [{"question": "If you are at -2 on a number line and you add 5, where do you end up?", "options": [{"a": "-7"}, {"b": "7"}, {"c": "3"}, {"d": "-3"}], "answer": "c"}, {"question": "Adding a positive number on the number line means moving in which direction?", "options": [{"a": "To the left"}, {"b": "To the right"}, {"c": "Up"}, {"d": "Down"}], "answer": "b"}, {"question": "What is -4 + 6?", "options": [{"a": "-10"}, {"b": "10"}, {"c": "-2"}, {"d": "2"}], "answer": "d"}, {"question": "A diver is 7 feet below the sea (-7 feet). She swims up 4 feet. What is her new position?", "options": [{"a": "-3 feet"}, {"b": "3 feet"}, {"c": "-11 feet"}, {"d": "11 feet"}], "answer": "a"}, {"question": "What is -5 + 5?", "options": [{"a": "10"}, {"b": "0"}, {"c": "-10"}, {"d": "1"}], "answer": "b"}]}', '2025-10-11 22:14:03.139391');
INSERT INTO public.assessments VALUES (29, 30, '{"no_of_questions": 4, "questions": [{"question": "What is the special name for a number that cannot be written as a simple fraction and has a decimal that goes on forever with no pattern?", "options": [{"a": "A whole number"}, {"b": "An irrational number"}, {"c": "A fraction number"}, {"d": "An even number"}], "answer": "b"}, {"question": "Which of these is the most famous example of an irrational number that we discussed?", "options": [{"a": "10"}, {"b": "3/4"}, {"c": "Pi (\u03c0)"}, {"d": "0.5"}], "answer": "c"}, {"question": "The digits of an irrational number after the decimal point...", "options": [{"a": "Always stop after a few numbers."}, {"b": "Repeat in a simple, clear pattern."}, {"c": "Are always the number 5."}, {"d": "Go on forever without a repeating pattern."}], "answer": "d"}, {"question": "The number 1/2 is a ''fraction-friendly'' number. Is it an irrational number?", "options": [{"a": "Yes, because it''s a number."}, {"b": "Yes, because it''s a fraction."}, {"c": "No, because it can be written as a simple fraction."}, {"d": "No, because it is smaller than 1."}], "answer": "c"}]}', '2025-10-11 22:17:24.9136');
INSERT INTO public.assessments VALUES (35, 36, '{"no_of_questions": 6, "questions": [{"question": "In the number 582, what is the value of the digit 8?", "options": [{"a": "8"}, {"b": "80"}, {"c": "800"}, {"d": "2"}], "answer": "b"}, {"question": "What is 245 + 132?", "options": [{"a": "377"}, {"b": "375"}, {"c": "277"}, {"d": "477"}], "answer": "a"}, {"question": "Solve this subtraction problem: 97 - 45 = ?", "options": [{"a": "42"}, {"b": "52"}, {"c": "62"}, {"d": "142"}], "answer": "b"}, {"question": "Which number has 4 hundreds, 0 tens, and 9 ones?", "options": [{"a": "490"}, {"b": "49"}, {"c": "409"}, {"d": "904"}], "answer": "c"}, {"question": "Maria has 36 stickers. Her friend gives her 25 more. How many stickers does Maria have in total?", "options": [{"a": "51"}, {"b": "11"}, {"c": "511"}, {"d": "61"}], "answer": "d"}, {"question": "Which number sentence can be used to check the answer to 50 + 30 = 80?", "options": [{"a": "80 + 30 = 110"}, {"b": "50 - 30 = 20"}, {"c": "80 - 30 = 50"}, {"d": "80 + 50 = 130"}], "answer": "c"}]}', '2025-10-11 22:20:30.511651');
INSERT INTO public.assessments VALUES (36, 37, '{"no_of_questions": 5, "questions": [{"question": "Look at the picture. There are 3 triangles and 4 squares. What is the ratio of triangles to squares?", "options": [{"a": "4:3"}, {"b": "3:4"}, {"c": "3:7"}, {"d": "7:3"}], "answer": "b"}, {"question": "In a fruit basket, there are 5 apples and 2 bananas. What is the ratio of apples to bananas?", "options": [{"a": "2:5"}, {"b": "5:7"}, {"c": "7:5"}, {"d": "5:2"}], "answer": "d"}, {"question": "A pet shop has 6 kittens and 5 puppies. What is the ratio of puppies to kittens?", "options": [{"a": "6:5"}, {"b": "5:11"}, {"c": "5:6"}, {"d": "6:11"}], "answer": "c"}, {"question": "How do you read the ratio 7:1?", "options": [{"a": "7 plus 1"}, {"b": "1 to 7"}, {"c": "7 minus 1"}, {"d": "7 to 1"}], "answer": "d"}, {"question": "There are 4 red cars and 9 blue cars in a parking lot. What is the ratio of red cars to blue cars?", "options": [{"a": "4:9"}, {"b": "9:4"}, {"c": "4:13"}, {"d": "13:4"}], "answer": "a"}]}', '2025-10-11 22:21:17.330933');
INSERT INTO public.assessments VALUES (19, 20, '{"no_of_questions": 4, "questions": [{"question": "Look at the number 5.2. What is the dot between the 5 and the 2 called?", "options": [{"a": "A period"}, {"b": "A comma"}, {"c": "A decimal point"}, {"d": "A star"}], "answer": "c"}, {"question": "A ten-frame grid has 3 parts colored in. What is the decimal for this picture?", "options": [{"a": "3.0"}, {"b": "0.3"}, {"c": "10.3"}, {"d": "3"}], "answer": "b"}, {"question": "The decimal 0.8 means...", "options": [{"a": "8 whole things"}, {"b": "Less than zero"}, {"c": "8 parts out of 10"}, {"d": "8 and 10"}], "answer": "c"}, {"question": "Which decimal shows that a ten-frame is completely full?", "options": [{"a": "0.1"}, {"b": "0.5"}, {"c": "1.0"}, {"d": "0.9"}], "answer": "c"}]}', '2025-10-11 22:10:27.678442');
INSERT INTO public.assessments VALUES (22, 23, '{"no_of_questions": 4, "questions": [{"question": "What is the main goal of descriptive writing?", "options": [{"a": "To tell a story with a beginning and an end."}, {"b": "To list facts about a topic."}, {"c": "To paint a picture in the reader''s mind using words."}, {"d": "To ask the reader a lot of questions."}], "answer": "c"}, {"question": "Which of these are the ''tools'' we use for descriptive writing?", "options": [{"a": "Pencils and crayons"}, {"b": "Our five senses"}, {"c": "Numbers and letters"}, {"d": "Our friends'' names"}], "answer": "b"}, {"question": "The sentence ''The kitten''s fur is very soft'' uses which sense?", "options": [{"a": "Sight"}, {"b": "Sound"}, {"c": "Smell"}, {"d": "Touch"}], "answer": "d"}, {"question": "''The loud bell rang.'' Which sense is being used to describe the bell?", "options": [{"a": "Taste"}, {"b": "Sound"}, {"c": "Sight"}, {"d": "Touch"}], "answer": "b"}]}', '2025-10-11 22:12:40.61261');
INSERT INTO public.assessments VALUES (27, 28, '{"no_of_questions": 6, "questions": [{"question": "A function machine has a rule. What does the ''input'' mean?", "options": [{"a": "The number that comes out"}, {"b": "The number you put in"}, {"c": "The rule of the machine"}, {"d": "The name of the machine"}], "answer": "b"}, {"question": "If a function machine''s rule is ''ADD 4'', and you put in the number 6, what is the output?", "options": [{"a": "2"}, {"b": "6"}, {"c": "10"}, {"d": "24"}], "answer": "c"}, {"question": "A machine takes the number 5 as an input and gives out the number 3. What is the rule?", "options": [{"a": "Add 2"}, {"b": "Multiply by 2"}, {"c": "Subtract 2"}, {"d": "Add 3"}], "answer": "c"}, {"question": "The rule is ''MULTIPLY BY 3''. If the input is 5, what is the output?", "options": [{"a": "8"}, {"b": "2"}, {"c": "15"}, {"d": "53"}], "answer": "c"}, {"question": "Look at this table: Input is 2, Output is 4. Input is 3, Output is 5. Input is 4, Output is 6. What is the rule?", "options": [{"a": "Multiply by 2"}, {"b": "Add 1"}, {"c": "Subtract 2"}, {"d": "Add 2"}], "answer": "d"}, {"question": "What is another name for the answer that comes out of a function machine?", "options": [{"a": "Output"}, {"b": "Input"}, {"c": "Rule"}, {"d": "Guess"}], "answer": "a"}]}', '2025-10-11 22:15:52.51416');
INSERT INTO public.assessments VALUES (34, 35, '{"no_of_questions": 4, "questions": [{"question": "What is intonation?", "options": [{"a": "How loudly you speak"}, {"b": "The words you choose"}, {"c": "The rise and fall of your voice"}, {"d": "How fast you talk"}], "answer": "c"}, {"question": "When you ask a question like ''Is that your dog?'', how does your voice usually go at the end?", "options": [{"a": "It goes up (rising intonation)"}, {"b": "It goes down (falling intonation)"}, {"c": "It stays flat"}, {"d": "It gets very quiet"}], "answer": "a"}, {"question": "When you make a statement like ''My favorite color is blue.'', how does your voice usually go at the end?", "options": [{"a": "It goes up (rising intonation)"}, {"b": "It goes down (falling intonation)"}, {"c": "It gets very loud"}, {"d": "It stays flat"}], "answer": "b"}, {"question": "The sentence ''She found it'' can be a statement or a question depending on the...", "options": [{"a": "First word"}, {"b": "Number of words"}, {"c": "Time of day"}, {"d": "Intonation"}], "answer": "d"}]}', '2025-10-11 22:19:43.517145');
INSERT INTO public.assessments VALUES (42, 43, '{"no_of_questions": 4, "questions": [{"question": "K\u00ed ni a m\u00e1a \u0144 p\u00e8 n\u00ed \u1ecd\u0300r\u1ecd\u0300 \u00ecj\u00ecnl\u1eb9\u0300 \u00e0w\u1ecdn \u00e0gb\u00e0?", "options": [{"a": "Orin"}, {"b": "\u00d2we"}, {"c": "\u00cct\u00e0n"}, {"d": "\u00c0l\u1ecd\u0301"}], "answer": "b"}, {"question": "Par\u00ed \u00f2we y\u00ec\u00ed: ''\u00c0gb\u00e0 k\u00ec \u00ed w\u00e0 l\u1ecd\u0301j\u00e0, k\u00ed or\u00ed \u1ecdm\u1ecd tuntun...''", "options": [{"a": "f\u1ecd\u0301"}, {"b": "d\u00e1n"}, {"c": "w\u1ecd\u0301"}, {"d": "t\u00f3bi"}], "answer": "c"}, {"question": "T\u00ed a b\u00e1 s\u1ecd p\u00e9 ''Ad\u00e9 fi oj\u00fa di \u00ecw\u00e9'', k\u00ed ni \u00ectum\u1ecd\u0300 r\u1eb9\u0300?", "options": [{"a": "\u00d3 j\u1eb9\u0301 ak\u1eb9\u0301k\u1ecd\u0300\u1ecd\u0301 gidi"}, {"b": "\u00d3 ti f\u1ecd\u0301j\u00fa"}, {"c": "\u00d3 k\u00f3 \u00ecw\u00e9 pa m\u1ecd\u0301"}, {"d": "\u00d3 k\u00f3r\u00ec\u00edra \u00ecw\u00e9"}], "answer": "a"}, {"question": "\u00c8wo n\u00edn\u00fa \u00ecw\u1ecd\u0300ny\u00ed ni \u00f3 j\u1eb9\u0301 \u00e0k\u00e0nl\u00f2 \u00e8d\u00e8?", "options": [{"a": "\u00cc\u1e63e ni \u00f2\u00f2g\u00f9n \u00ec\u1e63\u1eb9\u0301"}, {"b": "\u00d3 na \u00ecka p\u1eb9\u0301"}, {"c": "A k\u00ec \u00ed fi id\u00e0 pa \u00ecgb\u00edn"}, {"d": "Abiyam\u1ecd k\u00ec \u00ed gb\u1ecd\u0301 \u1eb9k\u00fan \u1ecdm\u1ecd r\u1eb9\u0300 k\u00f3 m\u00e1a ta ad\u00ec\u1eb9"}], "answer": "b"}]}', '2025-10-11 22:42:41.165424');
INSERT INTO public.assessments VALUES (20, 21, '{"no_of_questions": 3, "questions": [{"question": "What is a good way to greet your teacher in the morning?", "options": [{"a": "Goodbye"}, {"b": "Good morning"}, {"c": "Good night"}, {"d": "Thank you"}], "answer": "b"}, {"question": "If you want to learn a new friend''s name, what should you ask?", "options": [{"a": "How old are you?"}, {"b": "Where do you live?"}, {"c": "What is your name?"}, {"d": "Do you like to play?"}], "answer": "c"}, {"question": "Someone asks you, ''What is your name?''. What is the correct reply?", "options": [{"a": "I am in Grade 3."}, {"b": "I like ice cream."}, {"c": "My name is Alex."}, {"d": "It is sunny today."}], "answer": "c"}]}', '2025-10-11 22:11:03.977265');
INSERT INTO public.assessments VALUES (25, 26, '{"no_of_questions": 6, "questions": [{"question": "What does it mean for two shapes to be congruent?", "options": [{"a": "They have the same shape but a different size."}, {"b": "They have the same size but a different shape."}, {"c": "They have the exact same size and the exact same shape."}, {"d": "They both have straight lines."}], "answer": "c"}, {"question": "Look at a standard classroom door and a standard window. Are they congruent?", "options": [{"a": "Yes, because they are both rectangles."}, {"b": "No, because they have a different size."}, {"c": "Yes, because you can see through them."}, {"d": "No, because one is made of wood and one is made of glass."}], "answer": "b"}, {"question": "Which of these objects are most likely to be congruent?", "options": [{"a": "A car and a bicycle."}, {"b": "Two brand new pencils from the same box."}, {"c": "A dinner plate and a small saucer."}, {"d": "An adult''s shoe and a baby''s shoe."}], "answer": "b"}, {"question": "If you have a triangle and you turn it upside down, is it still congruent to the way it was before?", "options": [{"a": "No, because it is pointing a different way."}, {"b": "No, because turning it changes its shape."}, {"c": "Yes, because it is still the same size and the same shape."}, {"d": "Yes, but only if it''s a triangle."}], "answer": "c"}, {"question": "Imagine you trace your hand perfectly on a piece of paper and cut it out. The paper cutout of your hand is _______ to your actual hand.", "options": [{"a": "bigger than"}, {"b": "smaller than"}, {"c": "a different shape than"}, {"d": "congruent to"}], "answer": "d"}, {"question": "Which pair of shapes MUST be congruent?", "options": [{"a": "Any two squares."}, {"b": "Any two circles."}, {"c": "Two shapes that fit perfectly on top of each other."}, {"d": "Any two triangles."}], "answer": "c"}]}', '2025-10-11 22:14:52.293166');
INSERT INTO public.assessments VALUES (30, 31, '{"no_of_questions": 5, "questions": [{"question": "What is the main purpose of a summary?", "options": [{"a": "To copy the entire story"}, {"b": "To give only the most important information"}, {"c": "To share your personal opinion about the text"}, {"d": "To make the story longer"}], "answer": "b"}, {"question": "What is the very FIRST step you should take when writing a summary?", "options": [{"a": "Start writing immediately"}, {"b": "Read and understand the text"}, {"c": "Guess the main idea"}, {"d": "Write down all the details"}], "answer": "b"}, {"question": "Which of these should you LEAVE OUT of a summary?", "options": [{"a": "The main idea"}, {"b": "The name of the main character"}, {"c": "Small, unimportant details"}, {"d": "The main problem in the story"}], "answer": "c"}, {"question": "A summary should be...", "options": [{"a": "Longer than the original text"}, {"b": "Shorter than the original text"}, {"c": "Exactly the same length as the original text"}, {"d": "Filled with your own ideas"}], "answer": "b"}, {"question": "Read the text: ''Yesterday, Leo the lion was sleeping under a big, green tree. A tiny mouse ran over his nose and woke him up. Leo was angry, but the mouse promised to help him one day. Leo laughed and let the mouse go.'' Which is the BEST summary?", "options": [{"a": "A lion named Leo was sleeping under a tree."}, {"b": "A tiny mouse woke up an angry lion named Leo, and the lion let the mouse go."}, {"c": "The tree was big and green."}, {"d": "Leo the lion was angry and laughed."}], "answer": "b"}]}', '2025-10-11 22:17:45.552391');
INSERT INTO public.assessments VALUES (38, 39, '{"no_of_questions": 3, "questions": [{"question": "Ta ni Ol\u00f3d\u00f9mar\u00e8 r\u00e1n l\u00e1ti d\u00e1 ay\u00e9? (Who did God send to create the world?)", "options": [{"a": "\u1e62\u00e0ng\u00f3"}, {"b": "Od\u00f9duw\u00e0"}, {"c": "\u00d2g\u00fan"}, {"d": "\u1ecc\u0300b\u00e0t\u00e1l\u00e1"}], "answer": "b"}, {"question": "Ibo ni Od\u00f9duw\u00e0 k\u1ecd\u0301k\u1ecd\u0301 d\u00e1 il\u1eb9\u0300 s\u00ed? (Where did Oduduwa first create land?)", "options": [{"a": "\u00ccb\u00e0d\u00e0n"}, {"b": "\u00c8k\u00f3"}, {"c": "Ab\u1eb9\u0301\u00f2k\u00fata"}, {"d": "Il\u00e9-If\u1eb9\u0300"}], "answer": "d"}, {"question": "K\u00ed ni ad\u00ecy\u1eb9 (chicken) t\u00ed Od\u00f9duw\u00e0 m\u00fa w\u00e1 \u1e63e? (What did the chicken Oduduwa brought do?)", "options": [{"a": "\u00d3 f\u00f2 l\u1ecd (It flew away)"}, {"b": "\u00d3 y\u00e9 \u1eb9yin (It laid eggs)"}, {"c": "\u00d3 f\u1ecd\u0301n yanr\u00ecn n\u00e1\u00e0 k\u00e1 (It spread the sand)"}, {"d": "\u00d3 j\u1eb9un (It ate food)"}], "answer": "c"}]}', '2025-10-11 22:30:32.835826');
